import React, { useState } from 'react';
import { Box, Typography, Paper, Button, TextField, Autocomplete } from '@mui/material';
import styled from 'styled-components';

const CategoryContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  max-width: 800px;
  margin: 0 auto;
`;

const CategorySection = styled(Paper)`
  padding: 20px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
  }
`;

const SubcategorySection = styled(Paper)`
  padding: 15px;
  margin-top: 10px;
  background-color: #f5f5f5;
`;

const TopicSelection = ({ onTopicSelected }) => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubcategory, setSelectedSubcategory] = useState(null);
  const [customTopic, setCustomTopic] = useState('');
  const [showCustomInput, setShowCustomInput] = useState(false);

  const categories = {
    '스포츠': {
      '야구': ['메이저리그', 'KBO', '일본프로야구', '기타'],
      '축구': ['EPL', '라리가', '세리에A', '분데스리가', 'K리그', '기타'],
      '농구': ['NBA', 'KBL', '기타'],
      '테니스': ['ATP', 'WTA', '기타'],
      '골프': ['PGA', 'LPGA', '기타']
    },
    '음악': {
      '클래식': ['바로크', '고전주의', '낭만주의', '현대음악', '기타'],
      '재즈': ['스윙', '비밥', '쿨재즈', '퓨전재즈', '기타'],
      '팝': ['팝', '록', 'R&B', '힙합', '기타'],
      'K-POP': ['보이그룹', '걸그룹', '솔로', '기타'],
      '인디': ['인디팝', '인디록', '기타']
    },
    '영화': {
      '액션': ['슈퍼히어로', '무술', '전쟁', '기타'],
      '드라마': ['로맨스', '가족', '역사', '기타'],
      'SF': ['우주', '미래', 'AI', '기타'],
      '애니메이션': ['디즈니', '픽사', '일본애니', '기타'],
      '다큐멘터리': ['자연', '역사', '사회', '기타']
    },
    '과학': {
      '물리학': ['양자역학', '상대성이론', '천체물리학', '기타'],
      '생물학': ['유전학', '생태학', '신경과학', '기타'],
      '화학': ['유기화학', '무기화학', '생화학', '기타'],
      '천문학': ['태양계', '은하', '우주론', '기타'],
      '기술': ['AI', '로봇공학', '나노기술', '기타']
    },
    '문학': {
      '소설': ['추리', '로맨스', 'SF', '판타지', '기타'],
      '시': ['현대시', '고전시', '기타'],
      '에세이': ['인문', '자연', '여행', '기타'],
      '비평': ['문학비평', '영화비평', '기타'],
      '전기': ['역사인물', '현대인물', '기타']
    },
    '기타': {
      '직접입력': []
    }
  };

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setSelectedSubcategory(null);
    setShowCustomInput(false);
  };

  const handleSubcategoryClick = (subcategory) => {
    if (subcategory === '직접입력') {
      setShowCustomInput(true);
      setSelectedSubcategory(null);
    } else {
      setSelectedSubcategory(subcategory);
      setShowCustomInput(false);
    }
  };

  const handleCustomTopicSubmit = () => {
    if (customTopic.trim()) {
      onTopicSelected({
        main: selectedCategory,
        sub: '직접입력',
        specific: customTopic.trim()
      });
    }
  };

  const handleTopicSelect = (specific) => {
    onTopicSelected({
      main: selectedCategory,
      sub: selectedSubcategory,
      specific: specific
    });
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Typography variant="h5" gutterBottom>
        관심 있는 토픽을 선택해주세요
      </Typography>

      <CategoryContainer>
        {Object.keys(categories).map((category) => (
          <CategorySection
            key={category}
            elevation={2}
            onClick={() => handleCategoryClick(category)}
            sx={{
              backgroundColor: selectedCategory === category ? '#e3f2fd' : 'white'
            }}
          >
            <Typography variant="h6">{category}</Typography>
            {selectedCategory === category && (
              <SubcategorySection>
                {Object.keys(categories[category]).map((subcategory) => (
                  <Box key={subcategory} sx={{ mb: 2 }}>
                    <Typography
                      variant="subtitle1"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleSubcategoryClick(subcategory);
                      }}
                      sx={{
                        cursor: 'pointer',
                        color: selectedSubcategory === subcategory ? '#1976d2' : 'inherit',
                        '&:hover': { color: '#1976d2' }
                      }}
                    >
                      {subcategory}
                    </Typography>
                    {selectedSubcategory === subcategory && subcategory !== '직접입력' && (
                      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1, mt: 1 }}>
                        {categories[category][subcategory].map((specific) => (
                          <Button
                            key={specific}
                            variant="outlined"
                            size="small"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleTopicSelect(specific);
                            }}
                          >
                            {specific}
                          </Button>
                        ))}
                      </Box>
                    )}
                  </Box>
                ))}
                {showCustomInput && (
                  <Box sx={{ mt: 2, display: 'flex', gap: 2, alignItems: 'center' }}>
                    <TextField
                      fullWidth
                      label="관심 토픽을 입력하세요"
                      value={customTopic}
                      onChange={(e) => setCustomTopic(e.target.value)}
                      onKeyPress={(e) => {
                        if (e.key === 'Enter') {
                          handleCustomTopicSubmit();
                        }
                      }}
                    />
                    <Button
                      variant="contained"
                      onClick={handleCustomTopicSubmit}
                      disabled={!customTopic.trim()}
                    >
                      확인
                    </Button>
                  </Box>
                )}
              </SubcategorySection>
            )}
          </CategorySection>
        ))}
      </CategoryContainer>
    </Box>
  );
};

export default TopicSelection;
