import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material';
import CssBaseline from '@mui/material/CssBaseline';
import PreferenceForm from './components/PreferenceForm';
import ReadingPassage from './components/ReadingPassage';
import WordQuiz from './components/WordQuiz';
import ComprehensionQuiz from './components/ComprehensionQuiz';
import TopicSelection from './components/TopicSelection';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

function App() {
  const [userPreferences, setUserPreferences] = useState(null);
  const [selectedWords, setSelectedWords] = useState([]);
  const [currentModule, setCurrentModule] = useState('topicSelection');
  const [selectedTopic, setSelectedTopic] = useState(null);

  const handleTopicComplete = (topic) => {
    setSelectedTopic(topic);
    setCurrentModule('preferences');
  };

  const handlePreferencesSubmit = (preferences) => {
    setUserPreferences({ ...preferences, topic: selectedTopic });
    setCurrentModule('reading');
    // TODO: API call to get passage based on preferences and topic
  };

  const handleWordSelect = (words) => {
    setSelectedWords(words);
  };

  const handleReadingComplete = () => {
    setCurrentModule('wordQuiz');
    // TODO: Generate quiz based on selected words
  };

  const handleWordQuizComplete = (score) => {
    setCurrentModule('comprehension');
    // TODO: Generate comprehension questions
  };

  const handleComprehensionComplete = (score) => {
    setCurrentModule('complete');
    // TODO: Save progress and show completion message
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Routes>
          <Route path="/" element={
            <div className="App">
              {currentModule === 'topicSelection' && (
                <TopicSelection onComplete={handleTopicComplete} />
              )}
              
              {currentModule === 'preferences' && (
                <PreferenceForm 
                  onSubmit={handlePreferencesSubmit} 
                  selectedTopic={selectedTopic}
                />
              )}
              
              {currentModule === 'reading' && (
                <ReadingPassage
                  passage={{
                    title: "Sample Passage",
                    text: "This is a sample passage for testing. Click on words you don't know to add them to your vocabulary list.",
                    translation: "이것은 테스트를 위한 샘플 지문입니다. 알 수 없는 단어를 클릭하여 단어장에 추가하세요."
                  }}
                  onWordSelect={handleWordSelect}
                  onComplete={handleReadingComplete}
                />
              )}
              
              {currentModule === 'wordQuiz' && (
                <WordQuiz
                  words={[
                    {
                      word: "sample",
                      options: ["예시", "샘플", "모델", "테스트"],
                      correctAnswer: "샘플"
                    }
                  ]}
                  onComplete={handleWordQuizComplete}
                />
              )}
              
              {currentModule === 'comprehension' && (
                <ComprehensionQuiz
                  questions={[
                    {
                      id: 1,
                      question: "이 지문의 주제는 무엇인가요?",
                      options: ["테스트", "학습", "샘플", "예시"],
                      correctAnswer: "테스트"
                    }
                  ]}
                  onComplete={handleComprehensionComplete}
                />
              )}
              
              {currentModule === 'complete' && (
                <div style={{ textAlign: 'center', padding: '20px' }}>
                  <h2>학습 완료!</h2>
                  <p>수고하셨습니다. 다음 학습을 시작하시겠습니까?</p>
                  <button onClick={() => setCurrentModule('preferences')}>
                    새로운 학습 시작
                  </button>
                </div>
              )}
            </div>
          } />
        </Routes>
      </Router>
    </ThemeProvider>
  );
}

export default App;
