  useEffect(() => {
    if (selectedTopic) {
      const passages = getSamplePassages(selectedTopic.main, selectedTopic.sub, selectedTopic.subSub);
      setPassage(passages[currentLevel]);
    }
  }, [selectedTopic, currentLevel]);

  const handleWordClick = (word) => {
    setSelectedWords(prev => 
      prev.includes(word) 
        ? prev.filter(w => w !== word)
        : [...prev, word]
    );
  };

  const handleSubmit = () => {
    setIsSubmitting(true);
    const words = passage.split(' ').filter(word => word.length > 0);
    const unknownPercentage = (selectedWords.length / words.length) * 100;
    
    let nextLevel;
    if (unknownPercentage > 30) {
      nextLevel = levels[Math.max(0, levels.indexOf(currentLevel) - 1)];
    } else if (unknownPercentage < 10) {
      nextLevel = levels[Math.min(levels.length - 1, levels.indexOf(currentLevel) + 1)];
    } else {
      nextLevel = currentLevel;
    }

    if (nextLevel !== currentLevel) {
      setCurrentLevel(nextLevel);
      setSelectedWords([]);
    } else {
      onLevelDetermined(currentLevel, selectedWords);
      setTestComplete(true);
    }
    setIsSubmitting(false);
  };

  const handleNoUnknownWords = () => {
    const currentIndex = levels.indexOf(currentLevel);
    if (currentIndex < levels.length - 1) {
      setCurrentLevel(levels[currentIndex + 1]);
      setSelectedWords([]);
    } else {
      onLevelDetermined(currentLevel, []);
      setTestComplete(true);
    }
  };

  if (testComplete) {
    return (
      <Box sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="h5" gutterBottom>
          Test Complete
        </Typography>
        <Typography variant="body1">
          Your CEFR level has been determined. You can now proceed to the vocabulary learning module.
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h5" gutterBottom>
        CEFR Level Test
      </Typography>
      <Typography variant="subtitle1" gutterBottom>
        Topic: {selectedTopic.main} &gt; {selectedTopic.sub} &gt; {selectedTopic.subSub}
      </Typography>
      <Typography variant="subtitle2" gutterBottom>
        Current Level: {currentLevel}
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="body1" paragraph>
          {passage.split(' ').map((word, index) => (
            <span
              key={index}
              onClick={() => handleWordClick(word)}
              style={{
                cursor: 'pointer',
                backgroundColor: selectedWords.includes(word) ? '#ffeb3b' : 'transparent',
                padding: '2px',
                margin: '1px',
                borderRadius: '3px'
              }}
            >
              {word}
            </span>
          ))}
        </Typography>
      </Paper>

      <Box sx={{ display: 'flex', gap: 2, mb: 3 }}>
        <Button
          variant="contained"
          color="primary"
          onClick={handleSubmit}
          disabled={isSubmitting}
        >
          Submit Unknown Words
        </Button>
        <Button
          variant="outlined"
          color="primary"
          onClick={handleNoUnknownWords}
          disabled={isSubmitting}
        >
          I Know All Words
        </Button>
      </Box>

      {selectedWords.length > 0 && (
        <Paper elevation={3} sx={{ p: 3 }}>
          <Typography variant="subtitle1" gutterBottom>
            Selected Words:
          </Typography>
          <List>
            {selectedWords.map((word, index) => (
              <ListItem key={index}>
                <ListItemText primary={word} />
              </ListItem>
            ))}
          </List>
        </Paper>
      )}
    </Box>
  );
};

