import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  max-width: 600px;
  margin: 0 auto;
  padding: 20px;
`;

const Title = styled.h2`
  margin-bottom: 20px;
  color: #333;
`;

const StepIndicator = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 30px;
`;

const Step = styled.div`
  width: 30px;
  height: 30px;
  border-radius: 50%;
  background-color: ${props => props.active ? '#4CAF50' : '#ddd'};
  color: ${props => props.active ? 'white' : '#666'};
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
`;

const CategoryContainer = styled.div`
  margin-bottom: 20px;
`;

const CategoryTitle = styled.h3`
  margin-bottom: 15px;
  color: #444;
`;

const CategoryList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
  gap: 10px;
  margin-bottom: 20px;
`;

const CategoryButton = styled.button`
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  background-color: ${props => props.selected ? '#4CAF50' : 'white'};
  color: ${props => props.selected ? 'white' : '#333'};
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background-color: ${props => props.selected ? '#4CAF50' : '#f5f5f5'};
  }
`;

const CustomInput = styled.input`
  width: 100%;
  padding: 10px;
  border: 1px solid #ddd;
  border-radius: 5px;
  margin-top: 10px;
`;

const NavigationButtons = styled.div`
  display: flex;
  justify-content: space-between;
  margin-top: 30px;
`;

const Button = styled.button`
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  background-color: #4CAF50;
  color: white;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #45a049;
  }

  &:disabled {
    background-color: #cccccc;
    cursor: not-allowed;
  }
`;

const TopicSelection = ({ onComplete }) => {
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedCategories, setSelectedCategories] = useState({
    main: '',
    sub: '',
    final: ''
  });
  const [customInput, setCustomInput] = useState('');

  // 예시 카테고리 데이터
  const mainCategories = ['스포츠', '음악', '영화', '게임', '과학', '문학'];
  const subCategories = {
    '스포츠': ['프로야구', '프로축구', '농구', '배구'],
    '음악': ['팝', '록', '클래식', '재즈'],
    '영화': ['액션', '로맨스', 'SF', '코미디'],
    '게임': ['FPS', 'RPG', '전략', '스포츠'],
    '과학': ['물리학', '화학', '생물학', '천문학'],
    '문학': ['소설', '시', '에세이', '비평']
  };

  const finalCategories = {
    '프로야구': ['한화', '삼성', 'LG', '두산'],
    '프로축구': ['맨유', '리버풀', '바르셀로나', '레알마드리드'],
    '농구': ['NBA', 'KBL', 'NCAA', 'FIBA'],
    '배구': ['V-리그', '세계선수권', '올림픽', '월드컵']
  };

  const handleCategorySelect = (category) => {
    if (currentStep === 1) {
      setSelectedCategories({ ...selectedCategories, main: category });
    } else if (currentStep === 2) {
      setSelectedCategories({ ...selectedCategories, sub: category });
    } else {
      setSelectedCategories({ ...selectedCategories, final: category });
    }
    setCustomInput('');
  };

  const handleCustomInput = (e) => {
    setCustomInput(e.target.value);
  };

  const handleCustomSubmit = () => {
    if (customInput.trim()) {
      handleCategorySelect(customInput.trim());
    }
  };

  const handleNext = () => {
    if (currentStep < 3) {
      setCurrentStep(currentStep + 1);
    } else {
      onComplete(selectedCategories);
    }
  };

  const handleBack = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const getCurrentCategories = () => {
    if (currentStep === 1) return mainCategories;
    if (currentStep === 2) return subCategories[selectedCategories.main] || [];
    if (currentStep === 3) return finalCategories[selectedCategories.sub] || [];
    return [];
  };

  const getStepTitle = () => {
    if (currentStep === 1) return '주요 카테고리 선택';
    if (currentStep === 2) return `${selectedCategories.main}의 하위 카테고리 선택`;
    if (currentStep === 3) return `${selectedCategories.sub}의 세부 카테고리 선택`;
    return '';
  };

  return (
    <Container>
      <Title>선호 토픽 선택</Title>
      <StepIndicator>
        <Step active={currentStep >= 1}>1</Step>
        <Step active={currentStep >= 2}>2</Step>
        <Step active={currentStep >= 3}>3</Step>
      </StepIndicator>

      <CategoryContainer>
        <CategoryTitle>{getStepTitle()}</CategoryTitle>
        <CategoryList>
          {getCurrentCategories().map((category) => (
            <CategoryButton
              key={category}
              selected={
                (currentStep === 1 && selectedCategories.main === category) ||
                (currentStep === 2 && selectedCategories.sub === category) ||
                (currentStep === 3 && selectedCategories.final === category)
              }
              onClick={() => handleCategorySelect(category)}
            >
              {category}
            </CategoryButton>
          ))}
        </CategoryList>

        <CustomInput
          type="text"
          placeholder="원하는 카테고리를 직접 입력하세요"
          value={customInput}
          onChange={handleCustomInput}
          onKeyPress={(e) => e.key === 'Enter' && handleCustomSubmit()}
        />
      </CategoryContainer>

      <NavigationButtons>
        <Button onClick={handleBack} disabled={currentStep === 1}>
          이전
        </Button>
        <Button
          onClick={handleNext}
          disabled={
            (currentStep === 1 && !selectedCategories.main) ||
            (currentStep === 2 && !selectedCategories.sub) ||
            (currentStep === 3 && !selectedCategories.final)
          }
        >
          {currentStep === 3 ? '완료' : '다음'}
        </Button>
      </NavigationButtons>
    </Container>
  );
};

export default TopicSelection;
