import React, { useState, useEffect } from 'react';
import { Box, Typography, Paper, Button, CircularProgress } from '@mui/material';
import styled from 'styled-components';

const PassageContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 20px;
  line-height: 1.6;
  font-size: 1.1rem;
`;

const Word = styled.span`
  cursor: pointer;
  padding: 2px 4px;
  border-radius: 3px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #f0f0f0;
  }

  &.selected {
    background-color: #e3f2fd;
    border-bottom: 2px solid #2196f3;
  }
`;

const CEFRLevelTest = ({ selectedTopic, onLevelDetermined }) => {
  const [currentLevel, setCurrentLevel] = useState('B2'); // CEFR 레벨: A1, A2, B1, B2, C1, C2
  const [passage, setPassage] = useState('');
  const [selectedWords, setSelectedWords] = useState(new Set());
  const [isLoading, setIsLoading] = useState(true);
  const [wordCount, setWordCount] = useState(0);

  // CEFR 레벨별 샘플 텍스트 (실제로는 API에서 가져와야 함)
  const getSamplePassages = (topic) => {
    const passages = {
      '스포츠': {
        A1: "Baseball is a fun game. Players hit the ball with a bat. They run around the bases. Fans cheer for their team. The game has nine innings. Each team tries to score runs. The team with more runs wins. Baseball is popular in many countries. People enjoy watching games. Baseball brings people together.",
        A2: "Baseball is an exciting sport. Players wear uniforms and use special equipment. The pitcher throws the ball to the batter. Fielders try to catch the ball. Fans buy tickets to watch games. Teams play in big stadiums. Baseball games can last for hours. Players practice every day. Baseball is a team sport. Everyone has an important role.",
        B1: "Baseball is a strategic team sport that requires skill and coordination. Players must master various positions and techniques. The pitcher's role is crucial in controlling the game's pace. Batters need quick reflexes and good hand-eye coordination. Fielders must work together to prevent the opposing team from scoring. Baseball games often involve complex strategies and quick decision-making. Fans enjoy the excitement of close games and dramatic moments. Professional players train extensively to maintain peak performance. Baseball has a rich history and cultural significance in many countries. The sport continues to evolve with new training methods and technologies.",
        B2: "Baseball is a sophisticated sport that combines physical skill with mental strategy. The intricate relationship between pitching and batting requires deep understanding of game theory. Teams employ complex defensive formations based on statistical analysis of player tendencies. Modern baseball incorporates advanced metrics to evaluate player performance and team strategies. The psychological aspect of the game plays a crucial role in determining outcomes. Professional players undergo rigorous training regimens to maintain peak physical condition. Baseball's cultural significance extends beyond the field, influencing art, literature, and social dynamics. The sport's evolution reflects broader societal changes and technological advancements. Strategic decision-making often determines the outcome of closely contested games. Baseball's enduring popularity stems from its unique blend of individual excellence and team collaboration.",
        C1: "The strategic complexity of baseball transcends mere physical competition, embodying a sophisticated interplay of psychological warfare and statistical analysis. Advanced metrics and sabermetrics have revolutionized player evaluation and team management strategies. The intricate dynamics between pitcher and batter represent a microcosm of strategic decision-making under pressure. Professional organizations employ comprehensive analytics to optimize player development and game strategies. The psychological dimension of the sport manifests in various aspects, from individual performance under pressure to team dynamics and leadership. Baseball's cultural significance extends to its influence on social movements and national identity formation. The sport's evolution reflects broader societal transformations and technological innovations. Strategic decision-making often determines the outcome of high-stakes competitions. Baseball's enduring appeal lies in its unique synthesis of individual excellence and collective strategy.",
        C2: "The epistemological foundations of baseball strategy encompass a sophisticated synthesis of statistical analysis, psychological theory, and game theory principles. Advanced sabermetrics and machine learning algorithms have revolutionized the paradigm of player evaluation and team management. The intricate interplay between pitcher and batter constitutes a complex system of strategic decision-making under conditions of uncertainty and pressure. Professional organizations employ sophisticated analytics frameworks to optimize player development and tactical approaches. The psychological dimension of the sport manifests in multifaceted ways, from individual performance under extreme pressure to complex team dynamics and leadership paradigms. Baseball's cultural significance extends to its role in shaping national identity and social movements. The sport's evolution reflects broader societal transformations and technological innovations. Strategic decision-making often determines the outcome of high-stakes competitions. Baseball's enduring appeal lies in its unique synthesis of individual excellence and collective strategy."
      },
      '음악': {
        A1: "Music is nice to hear. People sing songs. They play instruments. Music makes us happy. We can dance to music. Music is everywhere. We hear music on the radio. People make music together. Music has different sounds. Everyone likes music.",
        A2: "Music is an important part of life. People listen to music every day. There are many types of music. Some people play instruments. Others sing songs. Music can make us feel different emotions. We hear music in movies and shows. Music brings people together. People enjoy concerts and festivals. Music is a universal language.",
        B1: "Music is a powerful form of artistic expression that connects people across cultures. Different genres of music reflect various cultural traditions and historical periods. Musicians spend years mastering their instruments and developing their skills. Music can evoke strong emotions and create lasting memories. The music industry has evolved significantly with digital technology. Live performances provide unique experiences for both artists and audiences. Music education plays an important role in cognitive development. Many people find comfort and inspiration in music during difficult times. The creation of music involves both technical skill and creative expression. Music continues to evolve with new technologies and cultural influences.",
        B2: "Music represents a complex form of artistic expression that transcends cultural and linguistic boundaries. The evolution of musical genres reflects broader social and historical developments. Professional musicians undergo extensive training to master their craft and develop unique artistic voices. Music's ability to evoke emotions and create shared experiences is well-documented in psychological research. The music industry has undergone significant transformation with the advent of digital technology. Live performances create unique opportunities for artistic expression and audience engagement. Music education contributes to cognitive development and emotional intelligence. Many individuals find solace and inspiration in music during challenging periods. The creative process of music composition involves both technical mastery and artistic innovation. Music continues to evolve in response to technological advancements and cultural shifts.",
        C1: "The intricate relationship between music and human cognition represents a sophisticated area of interdisciplinary research. The evolution of musical genres reflects complex social, historical, and technological developments. Professional musicians engage in extensive training to develop both technical proficiency and artistic expression. Music's capacity to evoke emotions and create shared experiences has been extensively studied in psychological and neurological research. The music industry has undergone profound transformation with digital technology and streaming platforms. Live performances create unique opportunities for artistic expression and audience engagement. Music education contributes significantly to cognitive development and emotional intelligence. Many individuals find profound meaning and inspiration in music during challenging periods. The creative process of music composition involves both technical mastery and artistic innovation. Music continues to evolve in response to technological advancements and cultural shifts.",
        C2: "The epistemological foundations of music's relationship with human cognition constitute a sophisticated area of interdisciplinary research. The evolution of musical genres reflects complex social, historical, and technological developments. Professional musicians engage in extensive training to develop both technical proficiency and artistic expression. Music's capacity to evoke emotions and create shared experiences has been extensively studied in psychological and neurological research. The music industry has undergone profound transformation with digital technology and streaming platforms. Live performances create unique opportunities for artistic expression and audience engagement. Music education contributes significantly to cognitive development and emotional intelligence. Many individuals find profound meaning and inspiration in music during challenging periods. The creative process of music composition involves both technical mastery and artistic innovation. Music continues to evolve in response to technological advancements and cultural shifts."
      }
      // 다른 토픽들도 추가 가능
    };

    return passages[topic.main] || passages['스포츠']; // 기본값으로 스포츠 텍스트 사용
  };

  useEffect(() => {
    // 실제 구현에서는 API를 통해 선택된 토픽에 맞는 텍스트를 가져와야 함
    const fetchPassage = async () => {
      setIsLoading(true);
      // API 호출 시뮬레이션
      setTimeout(() => {
        const passages = getSamplePassages(selectedTopic);
        setPassage(passages[currentLevel]);
        setWordCount(passages[currentLevel].split(/\s+/).length);
        setIsLoading(false);
      }, 1000);
    };

    fetchPassage();
  }, [currentLevel, selectedTopic]);

  const handleWordClick = (word) => {
    setSelectedWords(prev => {
      const newSet = new Set(prev);
      if (newSet.has(word)) {
        newSet.delete(word);
      } else {
        newSet.add(word);
      }
      return newSet;
    });
  };

  const handleSubmit = () => {
    const unknownWordPercentage = (selectedWords.size / wordCount) * 100;
    
    if (unknownWordPercentage > 10) {
      // 더 쉬운 레벨로 이동
      const levels = ['A1', 'A2', 'B1', 'B2', 'C1', 'C2'];
      const currentIndex = levels.indexOf(currentLevel);
      if (currentIndex > 0) {
        setCurrentLevel(levels[currentIndex - 1]);
        setSelectedWords(new Set());
      } else {
        // A1에서도 10% 이상이면 A1로 결정
        onLevelDetermined(currentLevel);
      }
    } else if (unknownWordPercentage < 5) {
      // 더 어려운 레벨로 이동
      const levels = ['A1', 'A2', 'B1', 'B2', 'C1', 'C2'];
      const currentIndex = levels.indexOf(currentLevel);
      if (currentIndex < levels.length - 1) {
        setCurrentLevel(levels[currentIndex + 1]);
        setSelectedWords(new Set());
      } else {
        // C2에서도 5% 미만이면 C2로 결정
        onLevelDetermined(currentLevel);
      }
    } else {
      // 적정 레벨 찾음 (5-10% 사이)
      onLevelDetermined(currentLevel);
    }
  };

  const renderPassage = () => {
    if (!passage) return null;

    return passage.split(/\s+/).map((word, index) => (
      <Word
        key={index}
        className={selectedWords.has(word) ? 'selected' : ''}
        onClick={() => handleWordClick(word)}
      >
        {word}{' '}
      </Word>
    ));
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Typography variant="h5" gutterBottom>
        CEFR 레벨 테스트
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="subtitle1" gutterBottom>
          현재 테스트 레벨: {currentLevel}
        </Typography>
        <Typography variant="body2" color="text.secondary" gutterBottom>
          모르는 단어를 클릭하세요. 전체 단어의 5-10%가 적정 수준입니다.
        </Typography>
      </Paper>

      {isLoading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
          <CircularProgress />
        </Box>
      ) : (
        <>
          <PassageContainer>
            {renderPassage()}
          </PassageContainer>

          <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <Typography variant="body2">
              선택한 단어: {selectedWords.size} / 전체 단어: {wordCount}
            </Typography>
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
            >
              다음
            </Button>
          </Box>
        </>
      )}
    </Box>
  );
};

export default CEFRLevelTest;
