import React, { useState } from 'react';
import { Box, Typography, Paper, Button } from '@mui/material';
import styled from 'styled-components';

const PassageContainer = styled.div`
  margin: 20px 0;
  padding: 20px;
  background-color: #f8f9fa;
  border-radius: 8px;
`;

const Word = styled.span`
  cursor: pointer;
  padding: 2px 4px;
  border-radius: 4px;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #e3f2fd;
  }

  &.selected {
    background-color: #bbdefb;
    text-decoration: underline;
  }
`;

const ReadingModule = ({ topic, level, onComplete }) => {
  const [selectedWords, setSelectedWords] = useState(new Set());

  // 샘플 지문 (실제로는 API에서 가져와야 함)
  const passage = "Baseball is a strategic team sport that requires skill and coordination. Players must master various positions and techniques. The pitcher's role is crucial in controlling the game's pace. Batters need quick reflexes and good hand-eye coordination. Fielders must work together to prevent the opposing team from scoring. Baseball games often involve complex strategies and quick decision-making. Fans enjoy the excitement of close games and dramatic moments. Professional players train extensively to maintain peak performance. Baseball has a rich history and cultural significance in many countries. The sport continues to evolve with new training methods and technologies.";

  const handleWordClick = (word) => {
    const newSelectedWords = new Set(selectedWords);
    if (newSelectedWords.has(word)) {
      newSelectedWords.delete(word);
    } else {
      newSelectedWords.add(word);
    }
    setSelectedWords(newSelectedWords);
  };

  const handleComplete = () => {
    onComplete(selectedWords);
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Typography variant="h5" gutterBottom>
        Reading Practice
      </Typography>

      <Paper elevation={3} sx={{ p: 3, mb: 3 }}>
        <Typography variant="subtitle1" gutterBottom>
          Topic: {topic.main} &gt; {topic.sub} &gt; {topic.specific}
        </Typography>
        <Typography variant="body2" color="text.secondary" gutterBottom>
          Level: {level}
        </Typography>
      </Paper>

      <PassageContainer>
        {passage.split(/\s+/).map((word, index) => (
          <Word
            key={index}
            className={selectedWords.has(word) ? 'selected' : ''}
            onClick={() => handleWordClick(word)}
          >
            {word}{' '}
          </Word>
        ))}
      </PassageContainer>

      <Box sx={{ mt: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="body2" color="text.secondary">
          Selected words: {selectedWords.size}
        </Typography>
        <Button
          variant="contained"
          color="primary"
          onClick={handleComplete}
          disabled={selectedWords.size === 0}
        >
          Next
        </Button>
      </Box>
    </Box>
  );
};

export default ReadingModule;
